#include <assert.h> #include <cctype> #include "classes.h" #include <vector> #include <string>#include <iterator>#include <stdio.h>#include <stdlib.h>Flight * populate_flight(const char * file); void PrintMessage();int menu();int main() { cout << "Version: 1.0 Term Project | Flight Management Program in C++ Produced by: Julien Rabino, Abdul Shakoor Raed, Aroush Qureshi" << endl; cout<<"\n <<<PRESS ENTER TO START PROGRAM>>>\n";cout<<endl;cin.get(); Flight * f = populate_flight("flight_info.txt"); static int map_count = 0;int choice = 1;    while(choice != 0){        switch(menu()){            case 1:				cout<<"Displaying Seat Map...\n\n";               f->showSeatMap(map_count);				PrintMessage();				                break;            case 2:				cout<<"Showing Passengers' Information...\n\n";               f->showPassengersInfo();                PrintMessage();				                break;               //every case needs a break            case 3:								f->addPassenger();      //flight f				cout<<"\n[Passenger successfully added]\n";                PrintMessage();                break;                       case 4:              f->removePassenger();			  			                PrintMessage();              break;             case 5:               f->saveFile();			   cout<<"\n[Data saved]\n";               PrintMessage();                break;             case 6:                choice = 0;                break;            break;                    }	}	delete f;} int menu(){   //menu function        int choice = -1;        cout << "Please select one of the following options: \n"<< endl;        cout << "1. Display flight Seat Map. \n" << endl;        cout << "2. Display Passenger's Information \n" << endl;        cout << "3. Add Passenger \n" << endl;				cout<< "4. Remove Passenger\n"<<endl;				cout<< "5. Save Data\n"<< endl;				cout<< "6. Exit\n" <<endl;				cout<< "Your choice is: ";        cin >> choice;		cout<<endl;		cout<<endl;		assert(choice <=6 && choice>= 1);        return choice;    }    Flight * populate_flight(const char * file){       ifstream in_stream(file);       if (in_stream.fail()){           cout<<"Sorry, cannot open the file."<< endl;           exit(1);       } 	in_stream.clear();      string flight_name;       in_stream>> flight_name;       string airline_name;   int row, col;  in_stream >> row >> col;   Airline a_line(flight_name,1, row, col);    Flight * A;int counter = 0; while (!in_stream.eof()){ string first_name, last_name, phone_num, seat_num, id; string record; getline(in_stream, record);   int i =0; // finding first names while(isalpha(record[i]) || isalpha(record[i+1]) ){   first_name += record[i]; i++; }       //finding last_names while (!isalpha(record[i])) i++; while(isalpha(record[i]) || isalpha(record[i+1]) ){ last_name += record[i]; i++; }   // finding phone_numbers while (!isdigit(record[i])) i++; while (isdigit(record[i]) || record[i] == '-'){ phone_num += record[i]; i++;   }   // finding seat numbers while (!isdigit(record[i])) i++; while (isalnum(record[i])){ seat_num += record[i]; i++;  }   while (!isdigit(record[i])) i++; // finding ids while (isdigit(record[i])){ id+= record[i]; i++; }     // does not populate on the first iteration *// if (counter != 0){   A = a_line.get_flights();    (*A).set_passenger_info( first_name, last_name, phone_num, seat_num,id); } counter ++; }   in_stream.close();return A; }       void PrintMessage(){	cout<<"\n <<<PRESS ENTER TO CONTINUE>>>\n";	cout<<endl;	cin.get();	cin.get();			}        